&lt;div class=&quot;container&quot;&gt; 
&lt;h1&gt;nPage pagination - Page - &lt;?php 
echo $npage-&gt;get_pageno(); // Get current page No 
?&gt;
&lt;/h1&gt;
&lt;?php if($data) { ?&gt; 
    &lt;table class=&quot;table table-hover&quot;&gt; 
    &lt;thead&gt; 
        &lt;tr&gt; 
        &lt;?php $keys = array_keys($data[0]); for ($i = 0; $i &lt; count($keys); $i++) { ?&gt; 
            &lt;th&gt;&lt;?php echo $keys[$i]; ?&gt;&lt;/th&gt; 
        &lt;?php } ?&gt; 
        &lt;/tr&gt; 
        &lt;/thead&gt; 
        &lt;tbody&gt; 
        &lt;?php 
            // print dynamic data 
            for ($i = 0; $i &lt; count($data); $i++) { ?&gt; 
            &lt;tr&gt;
            &lt;?php for ($j = 0; $j &lt; count($keys); $j++) { ?&gt; 
                &lt;td&gt;&lt;?php echo $data[$i][$keys[$j]]; ?&gt;&lt;/td&gt; 
            &lt;?php } ?&gt; 
            &lt;/tr&gt; 
        &lt;?php } ?&gt; 
        &lt;/tbody&gt; 
    &lt;/table&gt; 
&lt;?php } else { ?&gt; 
    No records 
&lt;?php } ?&gt; 
&lt;?php 
    echo $npage-&gt;get_navigation(); // print navigation button 
?&gt; 
&lt;/div&gt;
