function isMobile() {
  var result;
  result = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
  return result;
}
if(isMobile()){
    var video = document.querySelector("#demo_video");
    video.height = "auto";
    video.width = "auto";
}
